svg.script:
//
    // import "label namespace" from parent document
    var labelNamespace = location.hash.slice(1);
    labelNamespace && [].forEach.call(document.querySelectorAll('[data-label]'), function(element) {
      element.setAttribute('data-label', labelNamespace + ' -> ' + element.getAttribute('data-label'));
    });

    document.addEventListener('focus', function(event) {
      window.parent.iframeFocusEvent(event);
    }, true);
  //
